INSERT /* [AuthMapper.insertAuth] 사용자 토큰 추가 */
  INTO AUTH (
       USER_ID
     , AUTH_TOKEN
     , REG_ID
     , REG_DTM
     , UPD_ID
     , UPD_DTM
     )
       VALUES(
       #{admId}
     , #{token}
     , #{admId}
     , NOW()
     , #{admId}
     , NOW()
     )
    ON DUPLICATE KEY
UPDATE AUTH_TOKEN = #{token}
     , REG_DTM    = NOW()
     , UPD_DTM    = NOW()
